import './App.css'
import Day from './Day'
import { useState } from 'react'

function Calandar () {
  const [leftBorder, setLeftBorder] = useState(true)
  const [rightBorder, setRightBorder] = useState(true)

  const CalCtr = {
    height: '400px',
    width: '84vw',
    borderStyle: 'solid',
    borderRadius: '25px',
    display: 'flex',
    backgroundColor: 'blue',
    borderLeft: leftBorder ? 'solid' : '',
    borderRight: rightBorder ? 'solid' : '',
  }

  const dayDiv = {
    borderRight: '1px solid black'
  }
  
  return (
    <div style={CalCtr}>
      <Day border={setLeftBorder} css={dayDiv} />
      <Day />
      <Day />
      <Day />
      <Day />
      <Day />
      <Day border={setRightBorder} />
    </div>
  );
}

export default Calandar;
