import './App.js'
import { useState } from 'react'

function Day (props) {
  const [isHover, setHover] = useState(false)

  const DayCtr = {
    height: isHover ? '600px' : '400px',
    width: isHover ? '24vw' : '12vw',
    backgroundColor: isHover ? 'red' : '',
    borderRadius: isHover ? '25px' : '',
    // borderStyle: isHover ? 'solid' : '',
  }

  const mouseEnter = (props) => {
    if(props.border !== undefined) {
      props.border(false)
    }
    
    setHover(true)
  }

  const mouseLeave = (props) => {
    if(props.border !== undefined) {
      props.border(true)
    }

    setHover(false)
  }

  return (
    <div style={{...props.css, ...DayCtr}} onMouseEnter={() => mouseEnter(props)} onMouseLeave={() => mouseLeave(props)}>
      
    </div>
  )
}

export default Day;
