import './App.js'
import { useState } from 'react'

function Day (props) {
  const [isHover, setHover] = useState(false)

  const DayCtr = {
    height: isHover ? '600px' : '400px',
    width: isHover ? '24vw' : '12vw',
  }

  const stateFalse = () => {
    props.border(false)
    setHover(false)
  }

  const stateTrue = () => {
    props.border(true)
    setHover(true)
  }

  return (
    <div style={{...DayCtr, ...props.css}} onMouseEnter={() => stateFalse()} onMouseLeave={() => props.border(true)}>
      
    </div>
  )
}

export default Day;
