import './App.css'
import Day from './Day'
import { useState } from 'react'

const CalCtr = {
  height: '400px',
  width: '84vw',
  borderStyle: 'solid',
  borderRadius: '25px',
  display: 'flex',
  backgroundColor: 'blue'
}

const [border, setBorder] = useState(true)

const borderOff = () => {
  setBorder(false)
}

const borderOn = () => {
  setBorder(true)
}

function Calandar () {
  return (
    <div style={CalCtr}>
      <Day borderOff={borderOff} borderOn={borderOn} />
      <Day />
      <Day />
      <Day />
      <Day />
      <Day />
      <Day />
    </div>
  );
}

export default Calandar;
