import './App.css'
import Day from './Day'
import { useState } from 'react'

function Calandar () {
  const [border, setBorder] = useState(true)

  const borderOff = () => { setBorder(false) }
  const borderOn = () => { setBorder(true) }
  
  return (
    <div style={CalCtr}>
      <Day border={setBorder} />
      <Day />
      <Day />
      <Day />
      <Day />
      <Day />
      <Day />
    </div>
  );
}

export default Calandar;

const CalCtr = {
  height: '400px',
  width: '84vw',
  borderStyle: 'solid',
  borderRadius: '25px',
  display: 'flex',
  backgroundColor: border ? 'blue' : 'red'
}
